import React, { useState, useEffect } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import Error from './Error';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { CurrentUserContext, initialUser } from '../contexts/CurrentUserContext';
import api from '../utils/Api';

function App() {

  // переменные состояния видимости попапов
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  // переменные состояния открытия попапа карточки
  const [selectedCard, setSelectedCard] = useState(null);

  // переменные состояния пользователя
  const [currentUser, setCurrentUser] = useState(initialUser);

  // переменные состояния карточек
  const [cards, setCards] = useState([]);

  // далее обновляем стейт-переменную при вызове api.getUserInfo
  useEffect(() => {
    api.getUserInfo()
      .then((userData) => {
        setCurrentUser(userData);
      })
      .catch((err) => console.log(`Ошибка при получении данных пользователя: ${err}`));
  }, []);

      // получаем данные пользователя и карточки с сервера
      React.useEffect(() => {
        Promise.all([api.getUserInfo(), api.getInitialCards()])
            .then(([user, cards]) => {
                setUserName(user.name);
                setUserDescription(user.about);
                setUserAvatar(user.avatar);
                setCards(cards);
            })
            .catch((err) => {
                console.log(`Ошибка при получении данных: ${err}`);
                setErrorMessage('Произошла ошибка при получении данных');
            })
    }, []);

  // закрываем все попапы
  const closeAllPopups = () => {
    setSelectedCard(null);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
  }

  // функции открытия попапов
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(like => like._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((cards) => cards.map((c) => c._id === card._id ? newCard : c));
    })
      .catch((err) => { console.log(`Ошибка лайка: ${err}`)
      })
}

  return (

    <div className="page">
      <CurrentUserContext.Provider value={currentUser}>
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
        onCardLike={handleCardLike}
        cards={cards}
      />

      <PopupWithForm
        title="Редактировать профиль"
        name="edit-profile"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        buttonText="Сохранить"
        children={
          <div className="popup__form">
            <input 
            type="text" 
            className="popup__input popup__input_type_name" 
            id="profile-name" 
            name="name" 
            required 
            minLength="2" 
            maxLength="40" 
            placeholder="Имя" />
            <span className="popup__error profile-name-error"></span>
            <input 
            type="text" 
            className="popup__input popup__input_type_about" 
            id="profile-about" 
            name="about" 
            required 
            minLength="2" 
            maxLength="200" 
            placeholder="О себе" />
            <span className="popup__error profile-about-error"></span>
          </div>
        }
      />

      <PopupWithForm
        title="Новое место"
        name="add-card"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        buttonText="Создать"
        children={
          <div className="popup__form">
            <input 
            type="text" 
            className="popup__input popup__input_type_card-name" 
            id="card-name" 
            name="name" 
            required 
            minLength="2" 
            maxLength="30" 
            placeholder="Название" />
            <span className="popup__error card-name-error"></span>
            <input 
            type="url" 
            className="popup__input popup__input_type_image-src" 
            id="url" 
            name="link" 
            required 
            placeholder="Ссылка на картинку" />
            <span className="popup__error url-error"></span>
          </div>
        }
      />

      <PopupWithForm
        title="Вы уверены?"
        name="confirm"
        buttonText="Да"
      />

      <PopupWithForm 
      title="Обновить аватар"
      name="avatar"
      isOpen={isEditAvatarPopupOpen}
      onClose={closeAllPopups}
      buttonText="Сохранить"
      children={
        <div className="popup__form">
        <input 
        type="url" 
        className="popup__input popup__input_type_avatar" 
        id="urlav" 
        name="url" 
        placeholder="Ссылка на картинку" 
        required />
        <span className="popup__error urlav-error"></span>
        </div>
      }
      />

      <ImagePopup
        onClose={closeAllPopups}
        card={selectedCard}
      />

      <Footer />
      <Error />
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
